<h2>List of Feedbacks</h2><?php$query = $conn->query("SELECT `users`.`id`, `users`.`name`, `feedbacks`.`created_on`, `feedbacks`.`feedback`											 FROM `feedbacks`											 LEFT JOIN `users`											 ON `feedbacks`.`user_id` = `users`.`id`");$row = $query->fetchAll(PDO::FETCH_ASSOC);?><table class="table table-bordered table-responsive table-hover table-striped" id="usersTable">	<thead>		<tr>			<th width="160">Full Name</th>			<th width="160">Created On</th>			<th>Feedback</th>		</tr>	</thead>	<tfoot>		<tr>			<th colspan="3" class="ts-pager form-horizontal">				<button type="button" class="btn first"><i class="icon-step-backward glyphicon glyphicon-step-backward"></i></button>				<button type="button" class="btn prev"><i class="icon-arrow-left glyphicon glyphicon-backward"></i></button>				<span class="pagedisplay"></span> <!-- this can be any element, including an input -->				<button type="button" class="btn next"><i class="icon-arrow-right glyphicon glyphicon-forward"></i></button>				<button type="button" class="btn last"><i class="icon-step-forward glyphicon glyphicon-step-forward"></i></button>				<select class="pagesize input-mini" title="Select page size">				  <option selected="selected" value="10">10</option>				  <option value="25">25</option>				  <option value="50">50</option>				</select>				<select class="pagenum input-mini" title="Select page number"></select>			</th>		</tr>	</tfoot>	<tbody>		<?php		foreach ($row as $value) {		?>		<tr>			<td><a href="?p=user&id=<?php echo $value['id'] ?>"><?php echo $value['name'] ?></a></td>			<td><?php echo $value['created_on'] ?></td>			<td><?php echo nl2br($value['feedback']) ?></td>		</tr>		<?php		}		?>	</tbody></table>